public class PrintNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num= -20;
		
		if(num<0) {
			
			int convert = num * (-1);
			System.out.println("The Number" + num + " is converted to " + convert);
		}
				
				
	} 

}
